export type Option =
  | string
  | {
      disabled?: boolean
      label: string
      options?: Option[]
      custom?: boolean
      selected?: boolean
      value?: number | string
      [key: string]: unknown
    }

export type OptionsGroup = {
  label: string
  options?: Option[]
  [key: string]: unknown
}

export type Search =
  | 'external'
  | 'global'
  | {
      external?: boolean
      global?: boolean
    }
